const cheerio = require('cheerio');
const html = $input.item.json.data;
const $ = cheerio.load(html);

const articles = [];
let count = 0;
const maxArticles = 10; // Limit to 10 latest articles

// Prothom Alo article selectors
$('article, .story-card, .news-card').each(function() {
  if (count >= maxArticles) return false;

  const $article = $(this);

  // Extract title
  const titleElement = $article.find('h1, h2, h3, .title, .headline, a');
  const title = titleElement.first().text().trim();

  // Extract link
  let link = $article.find('a').first().attr('href');
  if (link && !link.startsWith('http')) {
    link = 'https://www.prothomalo.com' + (link.startsWith('/') ? link : '/' + link);
  }

  // Extract description/excerpt
  const desc = $article.find('p, .excerpt, .description').first().text().trim();

  // Extract image
  let image = $article.find('img').first().attr('src') || $article.find('img').first().attr('data-src');
  if (image && !image.startsWith('http')) {
    image = 'https://www.prothomalo.com' + (image.startsWith('/') ? image : '/' + image);
  }

  // Extract category
  const category = $article.find('.category, .section').first().text().trim() || '‡¶∏‡¶æ‡¶ß‡¶æ‡¶∞‡¶£';

  if (title && link && title.length > 10) {
    articles.push({
      source: 'Prothom Alo',
      source_url: 'https://www.prothomalo.com',
      title: title,
      link: link,
      description: desc || title.substring(0, 150) + '...',
      image: image || '',
      category: category,
      scraped_at: new Date().toISOString(),
      language: 'Bangla'
    });
    count++;
  }
});

if (articles.length === 0) {
  // Fallback: Try to find any links with Bangla text
  $('a').each(function() {
    if (count >= maxArticles) return false;

    const title = $(this).text().trim();
    const link = $(this).attr('href');

    // Check if title contains Bangla characters
