    if (hasBangla && title.length > 20 && link) {
      let fullLink = link;
      if (!link.startsWith('http')) {
        fullLink = 'https://www.prothomalo.com' + (link.startsWith('/') ? link : '/' + link);
      }

      articles.push({
        source: 'Prothom Alo',
        source_url: 'https://www.prothomalo.com',
        title: title,
        link: fullLink,
        description: title,
        image: '',
        category: '‡¶∏‡¶æ‡¶ß‡¶æ‡¶∞‡¶£',
        scraped_at: new Date().toISOString(),
        language: 'Bangla'
      });
      count++;
    }
  });
}

return articles.map(article => ({ json: article }));

