// Combine scraped article data with AI summary
const articleData = $('Validate Articles').item.json;
const aiOutput = $input.item.json.output || {};

// Parse AI output if it's a string
let summary = aiOutput;
if (typeof aiOutput === 'string') {
  try {
    summary = JSON.parse(aiOutput);
  } catch (e) {
    summary = {
      bangla_summary: aiOutput.substring(0, 200),
      english_summary: 'Summary not available',
      key_topics: [],
      sentiment: 'Neutral',
      main_theme: ''
    };
  }
}

// Format timestamp
const now = new Date();
const formattedDate = now.toLocaleDateString('en-GB', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});
const formattedTime = now.toLocaleTimeString('en-US', {
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

return [{
  json: {
    // Original article data
    source: articleData.source,
    source_url: articleData.source_url,
    title: articleData.title,
    link: articleData.link,
    original_description: articleData.description,
    image: articleData.image,
    category: articleData.category,
    language: articleData.language,

    // AI-generated summaries
    bangla_summary: summary.bangla_summary || '‡¶∏‡¶æ‡¶∞‡¶æ‡¶Ç‡¶∂ ‡¶™‡¶æ‡¶ì‡¶Ø‡¶º‡¶æ ‡¶Ø‡¶æ‡¶Ø‡¶º‡¶®‡¶ø',
    english_summary: summary.english_summary || 'Summary not available',
    key_topics: Array.isArray(summary.key_topics) ? summary.key_topics.join(', ') : '',
    sentiment: summary.sentiment || 'Neutral',
    main_theme: summary.main_theme || '',

    // Metadata
    scraped_at: articleData.scraped_at,
    processed_at: now.toISOString(),
    date: formattedDate,
    time: formattedTime,
    year: now.getFullYear(),
    month: now.getMonth() + 1,
    day: now.getDate()
  }
}];

// Generate summary statistics
const allItems = $input.all();
const totalArticles = allItems.length;

// Count by source
const sourceCount = {};
const sentimentCount = { Positive: 0, Negative: 0, Neutral: 0 };
const categoryCount = {};

for (const item of allItems) {
  // Count sources
  const source = item.json.source;
  sourceCount[source] = (sourceCount[source] || 0) + 1;

  // Count sentiment
  const sentiment = item.json.sentiment || 'Neutral';
  sentimentCount[sentiment] = (sentimentCount[sentiment] || 0) + 1;

  // Count categories
  const category = item.json.category;
  categoryCount[category] = (categoryCount[category] || 0) + 1;
}

return [{
  json: {
    summary: {
      total_articles: totalArticles,
      sources: sourceCount,
      sentiment_distribution: sentimentCount,
      categories: categoryCount,
      processed_at: new Date().toISOString(),
      status: 'Success'
    }
  }
}];
