// ============================================
// ğŸ” UNIVERSAL NEWS ARTICLE PARSER
// ============================================
// This parser works with ANY news source!
// It uses the selectors from the source config

const cheerio = require('cheerio');
const sourceConfig = $('Configure News Sources').item.json.source;
const html = $input.item.json.data;
const $ = cheerio.load(html);

const articles = [];
let count = 0;
const maxArticles = sourceConfig.maxArticles || 10;
const selectors = sourceConfig.selectors;

console.log(`\nğŸ“° Parsing: ${sourceConfig.name}`);
console.log(`URL: ${sourceConfig.url}`);
console.log(`Max Articles: ${maxArticles}`);

// ============================================
// PRIMARY PARSING STRATEGY
// ============================================
$(selectors.article).each(function() {
  if (count >= maxArticles) return false;

  const $article = $(this);

  // Extract title
  const titleElement = $article.find(selectors.title);
  const title = titleElement.first().text().trim();

  // Extract link
  let link = $article.find(selectors.link).first().attr('href');
  if (link && !link.startsWith('http')) {
    const baseUrl = new URL(sourceConfig.url);
    link = baseUrl.origin + (link.startsWith('/') ? link : '/' + link);
  }

  // Extract description
  const desc = $article.find(selectors.description).first().text().trim();

  // Extract image
  let image = $article.find(selectors.image).first().attr('src') || 
              $article.find(selectors.image).first().attr('data-src') ||
              $article.find(selectors.image).first().attr('data-lazy-src');
  if (image && !image.startsWith('http')) {
    const baseUrl = new URL(sourceConfig.url);
    image = baseUrl.origin + (image.startsWith('/') ? image : '/' + image);
  }

  // Extract category
  const category = $article.find(selectors.category).first().text().trim() || 'à¦¸à¦¾à¦§à¦¾à¦°à¦£';

  // Validate article has minimum required fields
  if (title && link && title.length > 10) {
    articles.push({
      source: sourceConfig.name,
      source_bangla: sourceConfig.nameInBangla,
      source_url: sourceConfig.url,
      title: title,
      link: link,
      description: desc || title.substring(0, 200) + '...',
      image: image || '',
      category: category,
      scraped_at: new Date().toISOString(),
      language: sourceConfig.language
    });
    count++;
  }
});

console.log(`âœ… Found ${articles.length} articles using primary selectors`);

// ============================================
// FALLBACK PARSING STRATEGY
// ============================================
// If primary parsing found nothing, try fallback
if (articles.length === 0) {
  console.log('âš ï¸ Primary parsing found 0 articles, trying fallback...');

  $('a').each(function() {
    if (count >= maxArticles) return false;

    const title = $(this).text().trim();
    const link = $(this).attr('href');

    // Check if title contains Bangla characters (for Bangla sources)
    const hasBangla = /[\u0980-\u09FF]/.test(title);
    const hasEnglish = /[a-zA-Z]/.test(title);

    const isValidLanguage = 
      (sourceConfig.language === 'Bangla' && hasBangla) ||
      (sourceConfig.language === 'English' && hasEnglish);

    if (isValidLanguage && title.length > 20 && link) {
      let fullLink = link;
      if (!link.startsWith('http')) {
        const baseUrl = new URL(sourceConfig.url);
        fullLink = baseUrl.origin + (link.startsWith('/') ? link : '/' + link);
      }

      articles.push({
        source: sourceConfig.name,
        source_bangla: sourceConfig.nameInBangla,
        source_url: sourceConfig.url,
        title: title,
        link: fullLink,
        description: title,
        image: '',
        category: 'à¦¸à¦¾à¦§à¦¾à¦°à¦£',
        scraped_at: new Date().toISOString(),
        language: sourceConfig.language
      });
      count++;
    }
  });

  console.log(`âœ… Fallback found ${articles.length} articles`);
}

console.log(`\nğŸ“Š Total articles extracted: ${articles.length}\n`);

return articles.map(article => ({ json: article }));

