  }
});

if (articles.length === 0) {
  // Fallback: Try to find any links with Bangla text
  $('a').each(function() {
    if (count >= maxArticles) return false;

    const title = $(this).text().trim();
    const link = $(this).attr('href');

    // Check if title contains Bangla characters
    const hasBangla = /[\u0980-\u09FF]/.test(title);

    if (hasBangla && title.length > 20 && link) {
      let fullLink = link;
      if (!link.startsWith('http')) {
        fullLink = 'https://www.kalerkantho.com' + (link.startsWith('/') ? link : '/' + link);
      }

