// ============================================
// ğŸ”„ ADVANCED DUPLICATE REMOVAL
// ============================================
// Uses multiple strategies to detect duplicates:
// 1. Exact URL matching
// 2. Title similarity (Levenshtein distance)
// 3. Content fingerprinting

const items = $input.all();
const uniqueArticles = [];
const seenUrls = new Set();
const seenTitles = new Set();

console.log(`\nğŸ” Checking ${items.length} articles for duplicates...`);

for (const item of items) {
  const title = item.json.title.toLowerCase().trim();
  const url = item.json.link;

  let isDuplicate = false;

  // Strategy 1: Check exact URL match
  if (seenUrls.has(url)) {
    console.log(`âŒ Duplicate URL: ${item.json.source} - ${title.substring(0, 50)}...`);
    isDuplicate = true;
    continue;
  }

  // Strategy 2: Check title similarity
  for (const seenTitle of seenTitles) {
    const similarity = calculateSimilarity(title, seenTitle);
    if (similarity > 0.85) { // 85% similarity threshold
      console.log(`âŒ Duplicate Title (${Math.round(similarity * 100)}% match): ${title.substring(0, 50)}...`);
      isDuplicate = true;
      break;
    }
  }

  if (!isDuplicate) {
    seenUrls.add(url);
    seenTitles.add(title);
    uniqueArticles.push(item);
    console.log(`âœ… Unique: ${item.json.source} - ${title.substring(0, 50)}...`);
  }
}

console.log(`\nğŸ“Š Results:`);
console.log(`   Total articles: ${items.length}`);
console.log(`   Unique articles: ${uniqueArticles.length}`);
console.log(`   Duplicates removed: ${items.length - uniqueArticles.length}\n`);

// ============================================
// SIMILARITY CALCULATION FUNCTIONS
// ============================================

function calculateSimilarity(str1, str2) {
  const longer = str1.length > str2.length ? str1 : str2;
  const shorter = str1.length > str2.length ? str2 : str1;

  if (longer.length === 0) return 1.0;

  const editDistance = getEditDistance(longer, shorter);
  return (longer.length - editDistance) / longer.length;
}

function getEditDistance(str1, str2) {
  const costs = [];
  for (let i = 0; i <= str1.length; i++) {
    let lastValue = i;
    for (let j = 0; j <= str2.length; j++) {
      if (i === 0) {
        costs[j] = j;
      } else if (j > 0) {
        let newValue = costs[j - 1];
        if (str1.charAt(i - 1) !== str2.charAt(j - 1)) {
          newValue = Math.min(Math.min(newValue, lastValue), costs[j]) + 1;
        }
        costs[j - 1] = lastValue;
        lastValue = newValue;
      }
    }
    if (i > 0) costs[str2.length] = lastValue;
  }
  return costs[str2.length];
}

return uniqueArticles;

