// ============================================
// ğŸ“ FORMAT FINAL DATA FOR STORAGE
// ============================================
// Combines scraped article data with AI summaries
// and formats everything for Google Sheets

const articleData = $('Validate Articles').item.json;
const aiOutput = $input.item.json.output || {};

// Parse AI output if it's a string
let summary = aiOutput;
if (typeof aiOutput === 'string') {
  try {
    summary = JSON.parse(aiOutput);
  } catch (e) {
    console.error('Failed to parse AI output:', e);
    summary = {
      original_summary: aiOutput.substring(0, 300),
      english_summary: 'Summary not available',
      key_topics: [],
      sentiment: 'Neutral',
      main_theme: ''
    };
  }
}

// Format timestamps
const now = new Date();
const formattedDate = now.toLocaleDateString('en-GB', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});
const formattedTime = now.toLocaleTimeString('en-US', {
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

// Calculate processing time
const scrapedAt = new Date(articleData.scraped_at);
const processingTimeMs = now - scrapedAt;
const processingTimeSec = (processingTimeMs / 1000).toFixed(2);

return [{
  json: {
    // ============================================
    // ARTICLE METADATA
    // ============================================
    source: articleData.source,
    source_bangla: articleData.source_bangla || articleData.source,
    source_url: articleData.source_url,
    language: articleData.language,

    // ============================================
    // ORIGINAL ARTICLE DATA
    // ============================================
    title: articleData.title,
    link: articleData.link,
    original_description: articleData.description,
    image: articleData.image,
    category: articleData.category,

    // ============================================
    // AI-GENERATED SUMMARIES
    // ============================================
    original_summary: summary.original_summary || 'à¦¸à¦¾à¦°à¦¾à¦‚à¦¶ à¦ªà¦¾à¦“à¦¯à¦¼à¦¾ à¦¯à¦¾à¦¯à¦¼à¦¨à¦¿',
    english_summary: summary.english_summary || 'Summary not available',
    key_topics: Array.isArray(summary.key_topics) 
      ? summary.key_topics.join(', ') 
      : '',
    sentiment: summary.sentiment || 'Neutral',
    main_theme: summary.main_theme || '',

    // ============================================
    // TIMESTAMPS & METADATA
    // ============================================
    scraped_at: articleData.scraped_at,
    processed_at: now.toISOString(),
    processing_time_sec: processingTimeSec,
    date: formattedDate,
    time: formattedTime,
    year: now.getFullYear(),
    month: now.getMonth() + 1,
    day: now.getDate(),
    weekday: now.toLocaleDateString('en-US', { weekday: 'long' })
  }
}];

