// ğŸ“Š GENERATE COMPREHENSIVE STATISTICS
// ============================================

const allItems = $input.all();
const totalArticles = allItems.length;

// Initialize counters
const sourceCount = {};
const languageCount = {};
const sentimentCount = { Positive: 0, Negative: 0, Neutral: 0 };
const categoryCount = {};
const topicCount = {};

// Calculate statistics
for (const item of allItems) {
  // Count by source
  const source = item.json.source;
  sourceCount[source] = (sourceCount[source] || 0) + 1;

  // Count by language
  const language = item.json.language;
  languageCount[language] = (languageCount[language] || 0) + 1;

  // Count by sentiment
  const sentiment = item.json.sentiment || 'Neutral';
  sentimentCount[sentiment] = (sentimentCount[sentiment] || 0) + 1;

  // Count by category
  const category = item.json.category;
  categoryCount[category] = (categoryCount[category] || 0) + 1;

  // Count topics
  const topics = item.json.key_topics ? item.json.key_topics.split(',') : [];
  topics.forEach(topic => {
    const cleanTopic = topic.trim();
    if (cleanTopic) {
      topicCount[cleanTopic] = (topicCount[cleanTopic] || 0) + 1;
    }
  });
}

// Find top 10 topics
const topTopics = Object.entries(topicCount)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10)
  .reduce((obj, [key, val]) => ({ ...obj, [key]: val }), {});

// Calculate percentages
const sentimentPercentages = {
  Positive: ((sentimentCount.Positive / totalArticles) * 100).toFixed(1) + '%',
  Negative: ((sentimentCount.Negative / totalArticles) * 100).toFixed(1) + '%',
  Neutral: ((sentimentCount.Neutral / totalArticles) * 100).toFixed(1) + '%'
};

